import React, { useEffect, useState } from "react";
import TodoList from "./TodoList";
import TodoForm from "./TodoForm";
import axios from "axios";
import type { TodoItemInterface } from "../types/types";
import { createTodo, deleteTodo, getTodos } from "../api/api";

const Todo: React.FC = () => {
  const [data, setData] = useState<TodoItemInterface[]>([]);
  const [todoText, setTodoText] = useState<string>("");
  const [todoId, setTodoId] = useState<number>(0);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      let apiData = await getTodos();
      setData(apiData);
    } catch (err) {
      console.log(err);
    }
  };

  const editTodoHandler = async (name: string, id: number) => {
    setTodoText(name);
    setTodoId(id);
  };

  const formSubmitHandler = async (name: string) => {
    try {
      await createTodo(name);
      setTodoText("");
      await fetchData();
    } catch (err) {
      console.log(err);
    }
  };

  const deleteTodoHandler = async (id: number) => {
    try {
      await deleteTodo(id);
      await fetchData();
    } catch (err) {
      console.log(err);
    }
  };

  console.log(data);

  return (
    <section className="todo-block">
      <TodoList todosData={data} deleteTodoItem={deleteTodoHandler} />
      <TodoForm
        setTodoText={setTodoText}
        todoText={todoText}
        formSubmit={formSubmitHandler}
      />
    </section>
  );
};

export default Todo;
